import React, { Component } from 'react'
import DogCard from "../components/DogCard"

export class dogBar extends Component {

  state = {
    pups: []
  }

  componentDidMount(){
    fetch("http://localhost:3000/pups")
    .then(resp => resp.json())
    .then(pups => {
      this.setState({
        pups: pups
      })
    })
  }

  handleClick = (pup) => {
    return <div></div>
    console.log(pup)
  }

  renderPups = () => {
    return this.state.pups.map(pup => {
      return <DogCard pup={pup} handleClick={this.handleClick}/>
    })
  }

  render() {
    return (
      <div id="dog-bar">
      {this.renderPups()}
     </div>
    )
  }
}

export default dogBar
