import React, { Component } from "react";
import "./App.css";
import DogBar from "./container/DogBar";
import DogInfo from "./components/DogInfo";

export class App extends Component {

  state = {
    pups: [],
    infoPup: {}
  };

  componentDidMount() {
    fetch("http://localhost:3000/pups")
      .then((resp) => resp.json())
      .then((pups) => {
        this.setState({
          pups: pups,
        });
      });
  }

  handleClick = (pup) => {
    this.setState({
      infoPup: pup
    })
    }

    goodDogClick = (pup) => {
      const reqObj = {
          method: "PATCH",
          headers: {
            "content-type": "application/json"
          },
          body: JSON.stringify({ isGoodDog: !pup.isGoodDog })
      }
      fetch(`http://localhost:3000/pups/${pup.id}`, reqObj)
      .then((resp) => resp.json())
      .then((infoPup) => {
       console.log("then", infoPup)
        // let updatedPup = this.state.pups.find (pup => {
        //   if (pup.id === infoPup.id)
        // })
      });
    }


  render() {
    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>
        <DogBar pups={this.state.pups} handleClick={this.handleClick}/>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          {/* <div id="dog-info"> */}
            <DogInfo goodDogClick={this.goodDogClick} pup={this.state.infoPup}/>
          {/* </div> */}
        </div>
      </div>
    );
  }
}

export default App;
